#define   FIREHAWK_I2C_DATA_REG_LSB_of(_x)                   (((_x) >> 0) & 0xff)
#define   FIREHAWK_I2C_DATA_REG_MSB_f(_x)                    (((_x) & 0xff) << 8)
#define   FIREHAWK_I2C_DATA_REG_MSB_of(_x)                   (((_x) >> 8) & 0xff)

#define FIREHAWK_MDIO_CONTROL_REG                            0x00032
#define   FIREHAWK_MDIO_CONTROL_REG_DATA_f(_x)               (((_x) & 0xffff) << 0)
#define   FIREHAWK_MDIO_CONTROL_REG_DATA_of(_x)              (((_x) >> 0) & 0xffff)
#define   FIREHAWK_MDIO_CONTROL_REG_READ_VALID               BIT(16)
#define   FIREHAWK_MDIO_CONTROL_REG_BUSY_STATUS              BIT(17)
#define   FIREHAWK_MDIO_CONTROL_REG_DEV_ADDR_f(_x)           (((_x) & 0x1f) << 18)
#define   FIREHAWK_MDIO_CONTROL_REG_DEV_ADDR_of(_x)          (((_x) >> 18) & 0x1f)
#define   FIREHAWK_MDIO_CONTROL_REG_PHY_ADDR_f(_x)           (((_x) & 0x1f) << 23)
#define   FIREHAWK_MDIO_CONTROL_REG_PHY_ADDR_of(_x)          (((_x) >> 23) & 0x1f)
#define   FIREHAWK_MDIO_CONTROL_REG_OP_CODE_f(_x)            (((_x) & 0x7) << 28)
#define   FIREHAWK_MDIO_CONTROL_REG_OP_CODE_of(_x)           (((_x) >> 28) & 0x7)

#define FIREHAWK_MDIO                                        0x00033
#define   FIREHAWK_MDIO_CLAUSE_45_ADDR_f(_x)                 (((_x) & 0xffff) << 0)
#define   FIREHAWK_MDIO_CLAUSE_45_ADDR_of(_x)                (((_x) >> 0) & 0xffff)
#define   FIREHAWK_MDIO_PREAMBLE_SEL_f(_x)                   (((_x) & 0x3) << 26)
#define   FIREHAWK_MDIO_PREAMBLE_SEL_of(_x)                  (((_x) >> 26) & 0x3)
#define   FIREHAWK_MDIO_MUX_SEL_f(_x)                        (((_x) & 0xf) << 28)
#define   FIREHAWK_MDIO_MUX_SEL_of(_x)                       (((_x) >> 28) & 0xf)

#endif /* FIREHAWK_H */
